package dolphin.android.apps.leetcodeplayground

import org.junit.Assert.assertEquals
import org.junit.Assert.assertTrue
import org.junit.Before
import org.junit.Test

// @Config(sdk = [Build.VERSION_CODES.P])
// @RunWith(AndroidJUnit4::class)
class Weekly197Test {
    private lateinit var solution: Weekly197Solution

    @Before
    fun setup() {
        solution = Weekly197Solution()
    }

    @Test
    fun wip() {
        assertTrue(!solution.wip())
        assertEquals(3 + 4, 4 + 3)
    }

    @Test
    fun `Number of Good Pairs - 1`() {
        assertEquals(4, solution.numIdenticalPairs(intArrayOf(1, 2, 3, 1, 1, 3)))
    }

    @Test
    fun `Number of Good Pairs - 2`() {
        assertEquals(6, solution.numIdenticalPairs(intArrayOf(1, 1, 1, 1)))
    }

    @Test
    fun `Number of Good Pairs - 3`() {
        assertEquals(0, solution.numIdenticalPairs(intArrayOf(1, 2, 3)))
    }

    @Test
    fun `Number of Substrings With Only 1s - 1`() {
        assertEquals(9, solution.numSub("0110111"))
    }

    @Test
    fun `Number of Substrings With Only 1s - 2`() {
        assertEquals(2, solution.numSub("101"))
    }

    @Test
    fun `Number of Substrings With Only 1s - 3`() {
        assertEquals(21, solution.numSub("111111"))
    }

    @Test
    fun `Number of Substrings With Only 1s - 4`() {
        assertEquals(0, solution.numSub("000"))
    }

//    @Test
//    fun `Number of Substrings With Only 1s - 5`() {
//        assertEquals(
//            200542505, solution.numSub(
//                ""
//            )
//        )
//    }

    @Test
    fun `Best Position for a Service Centre - 1`() {
        // positions = [[0,1],[1,0],[1,2],[2,1]]
        assertEquals(
            4.0, solution.getMinDistSum(
                arrayOf(
                    intArrayOf(0, 1), intArrayOf(1, 0), intArrayOf(1, 2), intArrayOf(2, 1)
                )
            ), 0.00001
        )
    }

    @Test
    fun `Best Position for a Service Centre - 2`() {
        // positions = [[1,1],[3,3]]
        assertEquals(
            2.82843, solution.getMinDistSum(
                arrayOf(
                    intArrayOf(1, 1), intArrayOf(3, 3)
                )
            ), 0.00001
        )
    }

    @Test
    fun `Best Position for a Service Centre - 3`() {
        // positions = [[1,1],[3,3]]
        assertEquals(
            0.0, solution.getMinDistSum(
                arrayOf(
                    intArrayOf(1, 1)
                )
            ), 0.00001
        )
    }

    @Test
    fun `Best Position for a Service Centre - 4`() {
        // positions = [1,1],[0,0],[2,0]
        // center = [1.0, 0.5773502711]
        assertEquals(
            2.73205, solution.getMinDistSum(
                arrayOf(
                    intArrayOf(1, 1), intArrayOf(0, 0), intArrayOf(2, 0)
                )
            ), 0.00001
        )
    }

    @Test
    fun `Best Position for a Service Centre - 5`() {
        // positions = [[0,1],[3,2],[4,5],[7,6],[8,9],[11,1],[2,12]]
        // center = [4.3460852395, 4.9813795505]
        assertEquals(
            32.94036, solution.getMinDistSum(
                arrayOf(
                    intArrayOf(0, 1),
                    intArrayOf(3, 2),
                    intArrayOf(4, 5),
                    intArrayOf(7, 6),
                    intArrayOf(8, 9),
                    intArrayOf(11, 1),
                    intArrayOf(2, 12)
                )
            ), 0.00001
        )
    }
}
